import React, { useEffect, useState } from 'react';
import axios from 'axios';
import styled from 'styled-components';
import { motion } from 'framer-motion';

const Select = styled(motion.select)`
    padding: 10px;
    margin: 10px 0;
    font-size: 1rem;
    border-radius: 4px;
    border: none;
    background: #333;
    color: #fff;
`;

// ÈùôÊÄÅÂÖúÂ∫ïÊï∞ÊçÆ
const FALLBACK_CATEGORIES = [
    { id: 1, name: 'Neon' },
    { id: 2, name: 'Marble' },
    { id: 3, name: 'Carbon Fiber' },
    { id: 4, name: 'Floral' },
    { id: 5, name: 'Retro' },
];

function CategoryFilter({ onChange }) {
    const [categories, setCategories] = useState([]);

    useEffect(() => {
        console.log('‚ñ∂Ô∏è CategoryFilter: ÂºÄÂßãËØ∑Ê±ÇÂàÜÁ±ª');
        axios.get('http://localhost:8081/api/categories')
            .then(response => {
                console.log('üì• Response data:', response.data);
                if (Array.isArray(response.data) && response.data.length > 0) {
                    setCategories(response.data);
                } else {
                    console.warn('‚ö†Ô∏è ÂêéÁ´ØËøîÂõûÁ©∫Êï∞ÁªÑÔºå‰ΩøÁî®ÂÖúÂ∫ïÊï∞ÊçÆ');
                    setCategories(FALLBACK_CATEGORIES);
                }
            })
            .catch(error => {
                console.error('‚ùå ËØ∑Ê±ÇÂàÜÁ±ªÂ§±Ë¥•:', error);
                setCategories(FALLBACK_CATEGORIES);
            });
    }, []);

    return (
        <Select
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 0.5 }}
            onChange={e => onChange(e.target.value)}
        >
            <option value="">All Categories</option>
            {categories.map(cat => (
                <option key={cat.id} value={cat.id}>
                    {cat.name}
                </option>
            ))}
        </Select>
    );
}

export default CategoryFilter;
