import React, { useEffect, useState } from 'react';
import axios from 'axios';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import ProductForm from './ProductForm';

const Section = styled.section`
  padding: 80px 20px;
  background: ${props => props.theme.colors.background};
  color: ${props => props.theme.colors.text};
  text-align: center;
  min-height: 60vh;
`;

const Title = styled.h2`
  font-size: 2rem;
  letter-spacing: 0.2rem;
  margin-bottom: 20px;
`;

const Button = styled.button`
  margin: 10px;
  padding: 10px 20px;
  background: ${props => props.theme.colors.accent};
  color: ${props => props.theme.colors.text};
  border: none;
  border-radius: 4px;
  cursor: pointer;
  font-size: 1rem;
  letter-spacing: 0.1rem;
  &:hover {
    opacity: 0.8;
  }
`;

const ProductGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(220px, 1fr));
  gap: 20px;
  margin-top: 30px;
`;

const ProductCard = styled(motion.div)`
  border: 1px solid ${props => props.theme.colors.text};
  padding: 20px;
  border-radius: 8px;
  cursor: pointer;
  &:hover {
    transform: scale(1.05);
  }
`;

function WorkSection() {
    const [products, setProducts] = useState([]);
    const [showForm, setShowForm] = useState(false);

    const fetchProducts = () => {
        axios.get('http://localhost:8081/api/products')
            .then(response => setProducts(response.data))
            .catch(error => console.error('Error fetching products:', error));
    };

    useEffect(() => {
        fetchProducts();
    }, []);

    const generateTestData = async () => {
        const testData = [
            {
                name: 'Neon Case',
                description: 'Bright neon phonecase with glowing edges',
                price: 15.99,
                stock: 50
            },
            {
                name: 'Marble Case',
                description: 'Stylish marble pattern phonecase',
                price: 25.49,
                stock: 30
            },
            {
                name: 'Carbon Fiber Case',
                description: 'Lightweight carbon fiber design',
                price: 29.99,
                stock: 20
            },
            {
                name: 'Floral Case',
                description: 'Colorful floral pattern, perfect for spring',
                price: 12.99,
                stock: 100
            },
            {
                name: 'Retro Case',
                description: 'Vintage 80s design with vibrant colors',
                price: 19.99,
                stock: 40
            }
        ];

        try {
            for (let product of testData) {
                await axios.post('http://localhost:8081/api/products', product);
            }
            alert('Test data generated successfully!');
            fetchProducts();
        } catch (error) {
            console.error('Error generating test data:', error);
            alert('Failed to generate test data.');
        }
    };

    const deleteProduct = async (id) => {
        try {
            await axios.delete(`http://localhost:8081/api/products/${id}`);
            fetchProducts();
        } catch (error) {
            console.error('Error deleting product:', error);
            alert('Failed to delete product.');
        }
    };

    return (
        <Section id="work">
            <Title>Our Work (Products)</Title>
            <div>
                <Button onClick={generateTestData}>Generate Test Data</Button>
                <Button onClick={() => setShowForm(!showForm)}>
                    {showForm ? 'Hide Form' : 'Add New Product'}
                </Button>
            </div>

            {showForm && <ProductForm onSuccess={fetchProducts} />}

            <ProductGrid>
                {products.length === 0 ? (
                    <p>No products available.</p>
                ) : (
                    products.map(product => (
                        <ProductCard
                            key={product.id}
                            initial={{ opacity: 0 }}
                            animate={{ opacity: 1 }}
                            transition={{ duration: 0.5 }}
                        >
                            <h3>{product.name}</h3>
                            <p>${product.price}</p>
                            <p>Stock: {product.stock}</p>
                            <Button onClick={() => deleteProduct(product.id)}>Delete</Button>
                        </ProductCard>
                    ))
                )}
            </ProductGrid>
        </Section>
    );
}

export default WorkSection;
